def square(num):
    return num ** 2

result = square(5)

def get_grade(score):
    if score >= 90:
        return 'A'
    elif score >= 80:
        return 'B'
    elif score >= 70:
        return 'C'
    else:
        return 'F'

student_grade = get_grade(85)

def is_even(number):
    if number % 2 == 0:
        return True
    else:
        return False

def calculate_discount(price, discount_rate):
    discounted_price = price - (price * discount_rate)
    return discounted_price

def check_vowel(letter):
    vowels = ['a', 'e', 'i', 'o', 'u']
    if letter.lower() in vowels:
        return True
    else:
        return False
